import React, { useState } from 'react';
import './ChatUI.css';

const ChatUI= ()=> {
  const [inputValue, setInputValue] = useState(''); // input 창에 입력된 내용
  const [template, setTemplate] = useState(''); // 채팅 내용 div에서 마지막 채팅과 input 창 사이에 insert될 내용
  
  let templateFront=`<div class="line"><span class="chat-box mine">`; // 추가되는 채팅의 html 형식 고정 앞부분
  let templateBack='</span></div>'; // 추가되는 채팅의 html 형식 고정 뒷부분

  const addChat = () => {
      setTemplate(templateFront+inputValue+templateBack);
      insertChat();
  };

  const insertChat = () => {
      let chatContent = document.querySelector('.chat-content');
      chatContent.insertAdjacentHTML('beforeend', template);
      setInputValue('');
  }

  return (
    <div class="chat-content">
        <div class="line">
            <span class="chat-box">안녕?</span>
        </div>
        <div class="line">
            <span class="chat-box mine">안녕!</span>
        </div>
        <input class="chat-box" value={inputValue} onChange={e=>setInputValue(e.target.value)}/>
        <button onClick={addChat}>전송</button>
    </div>
  );
}

export default ChatUI;
